import { createAction } from "redux-actions";

const CHANGE_INPUT = "subject/CHANGE_INPUT";
const ADD = "subject/ADD";
const REMOVE = "subject/REMOVE";
const FIX = "subject/FIX";

export const changeInput = createAction(CHANGE_INPUT, input => input);

let id = 3;

export const add = createAction(ADD, subject => ({
  id: id++,
  name: subject.name,
  credits: subject.credits,
  grade: subject.grade
}));

export const remove = createAction(REMOVE, id => id);

const initialState = {
  input: "",
  subject: [
    {
      id: 1,
      name: "자료구조",
      credits: 3,
      grade: 4.5
    },
    {
      id: 2,
      name: "알고리즘",
      credits: 3,
      grade: 3.5
    }
  ]
};

function subject(state = initialState, action) {
  switch (action.type) {
    case CHANGE_INPUT:
      return {
        ...state,
        input: action.input
      };
    case ADD:
      return {
        ...state,
        subject: state.subject.concat(action.subject)
      };
    case REMOVE:
      return {
        ...state,
        subject: state.subject.filter(it => it.id !== action.id)
      };
    case FIX:
      return {
        ...state,
        subject: state.subject.map(it =>
          it.id === action.id ? { ...it, grade: 0 } : it
        )
      };
    default:
      return { state };
  }
}

export default subject;
